
'use strict';
var proto = require('./proto');

// initialize the app
var App = (function (input, options) {
	this.listeners = this.listeners || [];
	this.options = options || {};
	this.cache = this.cache || {};

	if (typeof input !== 'undefined') {
		this.sel = this.query(input);
	}

	return this;
}).bind(Object.create(proto));

module.exports = App;